package operaters;

import java.util.Scanner;

public class compoundAssignment {

		public static void main(String[] args) {
			// TODO Auto-generated method stub
			Scanner sc =new Scanner(System.in);
			System.out.println("enter the number");
			 int a=sc.nextInt();
			a += 3;
			System.out.println("Result  +=3 is: "+a);
			a-= 6;
			System.out.println("Result  -=6 is: "+a);
			a *= 8;
			System.out.println("Result  *=8 is: "+a);
			a /= 9;
			System.out.println("Result  /=9 is: "+a);
			a %= 10;
			 System.out.println("Result %=10 is: "+a);
			           
			           
			           
		
			
			
			

		}

	}
